import React, { useEffect, useRef, useState } from "react";
import { auth, db } from "./firebase";
import { useAuthState } from "react-firebase-hooks/auth";
import { updateProfile } from "firebase/auth";
import { GoogleAuthProvider, signInWithPopup } from "firebase/auth";
import { addDoc, collection, serverTimestamp, query, orderBy, onSnapshot, limit, doc, deleteDoc} from "firebase/firestore";
import { getStorage, ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { Filter } from 'bad-words';

import './App.css';
import googleIcon from "./images/google.svg"
import logoutIcon from "./images/logout.svg"
import sendIcon from "./images/send.svg"
import replyIcon from "./images/reply.svg"
import backIcon from "./images/back.svg"
import nexilIcon from "./images/nexil-icon-transparent.png"

function UserEditRoom({ setRoom }){
	const [username, setUsername] = useState("");
	const [profilePicture, setProfilePicture] = useState(null);
	const textFilter = new Filter();

	const handleFileChange = (e) => {
    setProfilePicture(e.target.files[0]);
  };

	const saveUsername = (e) => {
		if(username.length > 3){
			updateProfile(auth.currentUser, {displayName: textFilter.clean(username)})
			setRoom(0)
		}else{
			alert("Username Should Be Atleast 3 Characters Long!")
		}
	}

const savePfp = async () => {
  if (!profilePicture) {
    alert("Please select a file first!");
    return;
  }

  const formData = new FormData();
  formData.append("image", profilePicture);

  try {
    // Upload to Imgur
    const response = await fetch("https://api.imgur.com/3/image", {
      method: "POST",
      headers: {
        Authorization: "c346806d43452cc", // Replace with your Imgur Client ID
      },
      body: formData,
    });

    const data = await response.json();

    if (data.success) {
      const downloadURL = data.data.link; // Get the uploaded image's URL
      await updateProfile(auth.currentUser, { photoURL: downloadURL });
      alert("Profile updated successfully!");
      setRoom(0);
    } else {
      console.error("Imgur upload failed:", data);
      alert("Failed to upload profile picture. Please try again.");
    }
  } catch (error) {
    console.error("Error uploading to Imgur:", error);
    alert("An error occurred. Please try again.");
  }
};

	return (
		<div className="h-screen flex justify-center items-center bg-neutral-950">
		<form onSubmit={() =>  saveUsername()}>
		<h1 className="text-3xl font-bold text-white text-center pb-4">Customize Your Profile</h1>
		<div className="flex">
			<p className="text-md font-bold text-white px-2">Username:</p>
			<input type="text" id="username" onChange={(e) => setUsername(e.target.value)} placeholder={auth.currentUser.displayName} />
		</div>
		<div className="flex justify-center items-center py-2">
			<button className="rounded-full bg-white hover:bg-purple-900 hover:text-white font-bold px-2 py-2" type="submit">Save Changes</button>
		<div className="px-2">
			<button onClick={() => setRoom(0)} className="rounded-full bg-white hover:bg-purple-900 hover:text-white font-bold px-2 py-2">Cancel</button>
		</div>
		</div>
		</form>
		</div>
	);
}

function NavHeader({ setRoom }){
	const [user] = useAuthState(auth);
	const signOut = () => {
		auth.signOut();
	};

	return (
		<div className="sticky top-0 text-white bg-neutral-900 flex items-center justify-between">
		<div className="flex items-center justify-center px-2 py-2">
		<img src={nexilIcon} alt="icon" className="w-8 h-8" />
			<h1 className="px-1 py-1 font-bold text-2xl">Nexil</h1>
		</div>
			<nav className="flex-shrink-0">
		{user ? (
			<div className="flex">
		<button onClick={signOut} type="button" className="border-0 bg-none px-2 py-2">
				<img src={logoutIcon} alt="logout" />
		</button>
		<button onClick={() => setRoom(10)}type="button" className="border-0 w-12 bg-none px-2 py-2">

				<img src={user.photoURL} alt="userPfp" className="rounded-full" />
		</button>
			</div>
		) : (
			<span></span>
		)}
			</nav>
		</div>
	);
}

function LoginAlert(){
	const [user] = useAuthState(auth);
	const googleSignIn = () => {
		const provider = new GoogleAuthProvider();
                signInWithPopup(auth, provider);
        };
        const signOut = () => {
		auth.signOut();
	};
	return (
		<div className="h-screen flex items-center justify-center">
			<div className="flex flex-col items-center">
		<div className="flex items-center justify-center mb-4">
		<img src={nexilIcon} className="w-1/2" />
		</div>
		<h1 className="font-bold text-white text-center py-2">🗣️ Talk with anyone using Nexil<br /> Get Started!</h1>
                        <button onClick={googleSignIn} className="border-0 bg-white text-black font-bold text-md hover:border-2 hover:border-blue-500 px-4 py-2 rounded-full flex items-center justify-center"><img src={googleIcon} className="w-5 h-5 mr-2"/>Log In With Google</button>
			</div>
		</div>
	);
}

const SendMessage = ({scroll, trigger}) => {
	const [message, setMessage] = useState("");
	const textFilter = new Filter();

	const sendMessage = async (event) => {
		event.preventDefault();
		if (message.trim() === "") {
			setMessage("");
			scroll.current.scrollIntoView({ behavior: "smooth" });
			return;
		}
		const { uid, displayName, photoURL } = auth.currentUser;
		await addDoc(collection(db, "nexil-chat-db"), {
			text: textFilter.clean(message),
			name: displayName,
			avatar: photoURL,
			createdAt: serverTimestamp(),
			uid,
		});
		setMessage("");
		scroll.current.scrollIntoView({ behavior: "smooth" });
	};
  return (
    <form onSubmit={(event) => sendMessage(event)} className="sticky bottom-0 bg-none flex items-center justify-between">
      <button className="transition ease-in-out delay-300 bg-neutral-800 px-2 py-2 hover:bg-neutral-900" onClick={() => trigger(0)}><img src={backIcon} alt="back" /></button>
      <label htmlFor="messageInput" hidden>
        Enter Message
      </label>
      <input
        id="messageInput"
        name="messageInput"
        type="text"
        className="bg-neutral-800 text-white border-0 px-2 py-2 w-screen h-full"
        placeholder="message"
	value={message}
        onChange={(e) => setMessage(e.target.value)}
      />
      <button type="submit" className="transition ease-in-out delay-300 bg-blue-600 px-2 py-2 hover:bg-purple-600"><img src={sendIcon} alt="send" /></button>
    </form>
  );
};

const MessageControls = ({ setMControl, message }) => {
	const [deleteAlertBox, setDAlertBox] = useState(false)
	const deleteMessage = async () => {
		await deleteDoc(doc(db, "nexil-chat-db", message.id))
		setDAlertBox(false)
	}
	return (
		<div>
		<button className="w-4 h-4 pr-5 rounded-full" onClick={() => setDAlertBox(true)}>🗑️ </button>
		<br />
		<button className="w-4 h-4 pr-5 rounded-full">🗨️</button>
		{deleteAlertBox ? (
		<div className="bg-black bg-opacity-70 rounded-md fixed inset-0 z-50 flex items-center justify-center">
		<div>
		<h1 className="text-xl font-bold text-white py-2">Do You Want To Delete This Message?</h1>
		<div className="flex justify-center">
		<div className="px-2">
		<button onClick={deleteMessage} className="bg-zinc-800 rounded-xl px-4 py-2 text-white text-center hover:bg-white hover:text-black">Yes</button>
		</div>
		<div className="px-2">
		<button onClick={() => setDAlertBox(false)} className="px-4 py-2 rounded-xl bg-zinc-800 text-white text-center hover:bg-white hover:text-black">No</button>
		</div>
		</div>
		</div>
		</div>
		) : null}
		</div>
	);
}

const ReceivedMessage = ({ message }) => {
	return (
      <div className="py-1 flex justify-start text-white">
		    <img 
		className="border-0 rounded-full mr-2 w-1/2 w-8 h-8" 
		src={message.avatar} 
		alt="userAvatar" 
		/>
    <div className="bg-neutral-800 border-0 rounded-b-md rounded-r-md max-w-[65%] inline-block">
      <div className="text-left px-1 py-1">
      <p className="italic font-bold text-sm">{message.name}</p>
      <p className="break-words">{message.text}</p>
      </div>
      </div>
      </div>
  );
};

const SentMessage = ({ message }) => {
	const [mControl, setMControl] = useState(false)
	const mControlTrigger = () => {
		if(mControl === true){
			setMControl(false)
		}else{
			setMControl(true)
		}
	}
	return (
      <div className="py-1 flex justify-end text-white">
		{mControl ? <MessageControls setMControl={setMControl} message={message} /> : null}
    <div className="bg-neutral-800 border-0 rounded-b-md rounded-l-md max-w-[65%] inline-block">
	<div className="text-right px-1 py-1" onClick={mControlTrigger}>
      <p className="italic font-bold text-sm">{message.name}</p>
      <p className="break-words">{message.text}</p>
		</div>
      </div>
		    <img 
		className="border-0 rounded-full ml-2 w-1/2 w-8 h-8" 
		src={message.avatar} 
		alt="userAvatar" 
		/>
      </div>
  );
}

const MessageInfoContainer = ({ message }) => {
  const [user] = useAuthState(auth);

  return (
    <div>
	  {message.uid === user.uid ? <SentMessage message={message} /> : <ReceivedMessage message={message}/>}
      </div>
  );
};

const MessageBody =  ({ setRoom }) => {
  const [messages, setMessages] = useState([]);
  const scroll = useRef();

  useEffect(() => {
    const q = query(
      collection(db, "nexil-chat-db"),
      orderBy("createdAt"),
    );

    const unsubscribe = onSnapshot(q, (QuerySnapshot) => {
      const fetchedMessages = [];
      QuerySnapshot.forEach((doc) => {
        fetchedMessages.push({ ...doc.data(), id: doc.id });
      });
      const sortedMessages = fetchedMessages.sort(
        (a, b) => a.createdAt - b.createdAt
      );
      setMessages(sortedMessages);
    });
    return () => unsubscribe;
  }, []);

  return (
	  <main onLoad={() => scroll.current.scrollIntoView({ behavior: "auto" })}>
	  <div>
	{messages?.map((message) => (
          <MessageInfoContainer key={message.id} message={message} />
        ))}
	  </div>
	  <span ref={scroll}></span>
          <SendMessage scroll={scroll} trigger={ setRoom }/>
	  </main>
  );
};

const PCRCard = ({ trigger }) => {
	return (
		<div className="max-w-sm p-6 bg-white border rounded-lg shadow bg-zinc-900 border-neutral-700 transition ease-in-out hover:shadow-lg hover:shadow-fuchsia-500/50">
    <h5 className="mb-2 text-2xl font-bold tracking-tight text-white">
      Public Chat Room
	</h5>
  <p className="mb-3 font-normal text-gray-400">
    A chat room to talk with multiple people from across the world.
  </p>
  <button className="transition ease-in-out delay-500 inline-flex items-center px-3 py-2 text-sm font-medium text-center text-white rounded-lg bg-gradient-to-r from-blue-800 to-fuchsia-500 hover:border-2 hover:border-blue-500" onClick={trigger} >
    Enter
    <svg
      className="rtl:rotate-180 w-3.5 h-3.5 ms-2"
      aria-hidden="true"
      xmlns="http://www.w3.org/2000/svg"
      fill="none"
      viewBox="0 0 14 10"
    >
      <path
        stroke="currentColor"
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeWidth={2}
        d="M1 5h12m0 0L9 1m4 4L9 9"
      />
    </svg>
  </button>
</div>
);
}

const Lobby = ({ enterRoom }) => {
	return (
		<div className="h-screen flex items-center justify-center">
			<PCRCard trigger={() => enterRoom(1)}/>
		</div>

	);
}

const PublicChatRoom = ({ setRoom }) => {
	return(
		<div>
			<MessageBody setRoom={setRoom} />
		</div>
	);
}

const LoadingPage = () => {
	return (
		<div className="h-screen flex items-center justify-center">
			<h1 className="font-bold text-3xl text-white">🌐 Loading...</h1>
		</div>
	);
}

function App() {
  const [user] = useAuthState(auth);
  const [room, setRoom] = useState(0);
  const [loading, setLoading] = useState(false);
  const [targetRoom, setTargetRoom] = useState(null);

  const enterRoom = (roomId) => {
    setTargetRoom(roomId);
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      setRoom(roomId);
    }, 500);
  };

  return (
    <div className="App bg-neutral-950">
	  <NavHeader setRoom={setRoom}/>
	  {!user ? (
        <LoginAlert />
      ) : loading ? (
        <LoadingPage />
      ) : room === 10 ? (
        <UserEditRoom setRoom={setRoom} />
      ) : room === 0 ? (
        <Lobby enterRoom={enterRoom} />
      ) : room === 1 ? (
        <PublicChatRoom setRoom={setRoom} />
      ) : (
	<Lobby enterRoom={enterRoom} />
      )}
    </div>
  );
}

export default App;
