import './App.css';
import React, { useState } from "react";
import { auth } from "./firebase";
import { useAuthState } from "react-firebase-hooks/auth";
import { GoogleAuthProvider, signInWithRedirect } from "firebase/auth";

function NavHeader(){
	const [user] = useAuthState(auth);
	const googleSignIn = () => {
		const provider = new GoogleAuthProvider();
		signInWithRedirect(auth, provider);
	};
	const signOut = () => {
		auth.signOut();
	};

	return (
		<div class="text-white bg-blue-500 flex items-center justify-between">
			<h1 class="px-2 py-2 font-bold text-2xl">Nexil</h1>
			<nav>
		{user ? (
			<button onClick={signOut} className="sign-out" type="button" class="border-0 bg-white text-black font-bold text-md hover:border-2 hover:outline-black">Sign Out</button>
		) : (
			<button className="sign-in" onClick={googleSignIn} class="border-0 bg-white text-black font-bold text-md hover:border-2 hover:outline-black">Sign In</button>
		)}
			</nav>
		</div>
	);
}
function LoginAlert(){
	const [user] = useAuthState(auth);                                    const googleSignIn = () => {                                                  const provider = new GoogleAuthProvider();
                signInWithRedirect(auth, provider);
        };
        const signOut = () => {                                                       auth.signOut();                                               };                                                                                                                                          return (                                                                      <div class="h-screen flex items-center justify-center">
                {user ? (                                                                     <button onClick={signOut} className="sign-out" type="button" class="border-0 bg-white text-black font-bold text-md hover:border-2 hover:outline-black">Sign Out</button>
                ) : (
                        <button className="sign-in" onClick={googleSignIn} class="border-0 bg-white text-black font-bold text-md hover:border-2 hover:outline-black">Sign In</button>                                             )}
		</div>                                                        );
}

function Body(){
	return(
		<div class="text-white h-screen flex items-center justify-center">
			<h1 class="italic">Nothing To See Here :)</h1>
		</div>
	);
}

function App() {
	const [user] = useAuthState(auth);
  return (
    <div className="App" class="bg-gray-900">
	  <NavHeader />
	  {!user ? <LoginAlert /> : <Body />}
    </div>
  );
}

export default App;
