import React, { useState } from "react";
import { auth } from "./firebase";
import { useAuthState } from "react-firebase-hooks/auth";
import { GoogleAuthProvider, signInWithRedirect } from "firebase/auth";
import './App.css';

function NavHeader(){
	const [user] = useAuthState(auth);
	const signOut = () => {
		auth.signOut();
	};

	return (
		<div className="text-white bg-blue-500 flex items-center justify-between">
			<h1 className="px-2 py-2 font-bold text-2xl">Nexil</h1>
			<nav>
		{user ? (
			<button onClick={signOut} type="button" className="sign-out border-0 bg-white text-black font-bold text-md hover:border-2 hover:outline-black px-2 py-2 rounded-full">Sign Out</button>
		) : (
			<span></span>
		)}
			</nav>
		</div>
	);
}

function UserAlert(){
	const [user] = useAuthState(auth);
	const googleSignIn = () => {
		const provider = new GoogleAuthProvider();
                signInWithPopup(auth, provider);
        };
        const signOut = () => {
		auth.signOut();
	};
	return (
		<div className="h-screen flex items-center justify-center">
                        <button onClick={googleSignIn} className="sign-in border-0 bg-white text-black font-bold text-md hover:border-2 hover:outline-black px-2 py-2 rounded-full">Sign In</button>
		</div>                                                        );
}

function Body(){
	return(
		<div className="text-white h-screen flex items-center justify-center">
			<h1 className="italic">Nothing To See Here :)</h1>
		</div>
	);
}

function App() {
  const [user] = useAuthState(auth);
  return (
    <div className="App bg-gray-900">
	  <NavHeader />
	  {!user ? <UserAlert /> : <Body />}
    </div>
  );
}

export default App;
