import React, { useState } from "react";
import { auth } from "./firebase";
import { useAuthState } from "react-firebase-hooks/auth";
import { GoogleAuthProvider, signInWithPopup } from "firebase/auth";

import './App.css';
import googleIcon from "./images/google.svg"
import logoutIcon from "./images/logout.svg"

function NavHeader(){
	const [user] = useAuthState(auth);
	const signOut = () => {
		auth.signOut();
	};

	return (
		<div className="text-white bg-blue-500 flex items-center justify-between">
			<h1 className="px-2 py-2 font-bold text-2xl">Nexil</h1>
			<nav>
		{user ? (
			<button onClick={signOut} type="button" className="sign-out border-0 bg-none px-2 py-2">
				<img src={logoutIcon} />
			</button>
		) : (
			<span></span>
		)}
			</nav>
		</div>
	);
}

function UserAlert(){
	const [user] = useAuthState(auth);
	const googleSignIn = () => {
		const provider = new GoogleAuthProvider();
                signInWithPopup(auth, provider);
        };
        const signOut = () => {
		auth.signOut();
	};
	return (
		<div className="h-screen flex items-center justify-center">
			<div>
		<h1 className="font-bold text-white py-2">üó£Ô∏è Talk with anyone using Nexil<br /> Get Started!</h1>
                        <button onClick={googleSignIn} className="sign-in border-0 bg-white text-black font-bold text-md hover:border-2 hover:border-blue-500 px-2 py-2 rounded-full flex items-center justify-center"><img src={googleIcon} className="w-1/5 pr-2"/> Sign In With Google</button>
			</div>
		</div>
	);
}

const SendMessage = () => {
  return (
    <form className="align-bottom send-message bg-none flex items-center justify-between">
      <label htmlFor="messageInput" hidden>
        Enter Message
      </label>
      <input
        id="messageInput"
        name="messageInput"
        type="text"
        className="form-input__input bg-white border-0 focus:border-2 focus:border-blue-500 px-2 py-2 w-11/12"
        placeholder="message"
      />
      <button type="submit" className="bg-blue-500 px-2 py-2 hover:bg-blue-900">Send</button>
    </form>
  );
};

function Body(){
	return(
		<div class="bg-gray-900">
			<div className="h-screen"></div>
			<SendMessage />
		</div>
	);
}

function App() {
  const [user] = useAuthState(auth);
  return (
    <div className="App bg-gray-900">
	  <NavHeader />
	  {!user ? <UserAlert /> : <Body />}
    </div>
  );
}

export default App;
