function App() {
  const [user] = useAuthState(auth);
  const [room, setRoom] = useState(0);
  const [loading, setLoading] = useState(false);
  const [targetRoom, setTargetRoom] = useState(null);
  const [dbName, setDBName] = useState("");

  const enterRoom = (roomId) => {
    setTargetRoom(roomId);
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      setRoom(roomId);
    }, 500);
  };

  return (
    <div className="App bg-neutral-950">
	  <NavHeader setRoom={setRoom}/>
	  {!user ? (
        <LoginAlert />
      ) : loading ? (
        <LoadingPage />
      ) : room === 10 ? (
        <UserEditRoom setRoom={setRoom} />
      ) : room === 0 ? (
        <Lobby setRoom={setRoom} enterRoom={enterRoom} setDBName={setDBName} />
      ) : room === 1 ? (
        <PublicChatRoom setRoom={setRoom} />
      ) : room === 2 ? (
	<PrivateRoom setRoom={setRoom} dbName={dbName}/>
      ) : (
	<Lobby setRoom={setRoom} enterRoom={enterRoom} setDBName={setDBName}/>
      )}
    </div>
  );
}

export default App;
