const MessageBody =  ({ setRoom, readDB, pf }) => {
  const [messages, setMessages] = useState([]);
  const scroll = useRef();

  useEffect(() => {
    const q = query(
      collection(db, readDB),
      orderBy("createdAt"),
    );

    const unsubscribe = onSnapshot(q, (QuerySnapshot) => {
      const fetchedMessages = [];
      QuerySnapshot.forEach((doc) => {
        fetchedMessages.push({ ...doc.data(), id: doc.id });
      });
      const sortedMessages = fetchedMessages.sort(
        (a, b) => a.createdAt - b.createdAt
      );
      setMessages(sortedMessages);
    });
    return () => unsubscribe;
  }, []);

  return (
	  <main onLoad={() => scroll.current.scrollIntoView({ behavior: "auto" })}>
	  <div>
	{messages?.map((message) => (
          <MessageInfoContainer key={message.id} message={message} dbName={readDB} pf={pf} />
        ))}
	  </div>
	  <span ref={scroll}></span>
          <SendMessage scroll={scroll} trigger={ setRoom } readDB={ readDB }/>
	  </main>
  );
};


